PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `users` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `username` TEXT NOT NULL UNIQUE, `email` TEXT NOT NULL UNIQUE, `password` TEXT NOT NULL, `permission` TEXT, `salt` TEXT NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `UserProfiles` (`userid` INTEGER PRIMARY KEY, `username` TEXT NOT NULL UNIQUE, `profilepicture` TEXT, `profilebanner` TEXT, `description` TEXT, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Cookbooks` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `userid` INTEGER NOT NULL, `cookbookname` TEXT NOT NULL, `recipes` TEXT, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Comments` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `comment` TEXT NOT NULL, `username` TEXT NOT NULL, `userid` INTEGER NOT NULL, `recipeid` INTEGER NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Recipes` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `userid` INTEGER NOT NULL, `title` TEXT NOT NULL, `time` TEXT NOT NULL, `ingredients` TEXT NOT NULL, `steps` TEXT NOT NULL, `tags` TEXT, `description` TEXT, `image` TEXT, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Ratings` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `score` INTEGER NOT NULL, `userid` INTEGER NOT NULL, `recipeid` INTEGER NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Tags` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` TEXT NOT NULL UNIQUE, `tagcolor` INTEGER NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
DELETE FROM sqlite_sequence;
COMMIT;
